const dbd = require("./src/index") 
const fs = require("fs") 
const db = new dbd.Database({
    tables: [{
        name: "test" 
    }], 
    maxFileData: 3000,
    timestamp: false, 
    debug: true 
}) 

db.on("ready", () => {
    
    
    console.log(`Database ready`)
    
    
})
db.on("debug", data => {
    console.log(data) 
})


process.on("exit", () => {
    console.log("exited") 
})
    
db.connect() 